import { PlacesContext } from "../context/place-context";
import classes from "./CityItem.module.css";

const CityItem = (props) => {
  const placesCtx = useContext(PlacesContext);
  const [cityData, setCityData] = useState({});

  useEffect(() => {
    placesCtx.favoritePlaces.forEach(async (place) => {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/onecall?lat=${place.lat}&lon=${place.long}&units=metric&appid=${process.env.REACT_APP_API_KEY}`
      );

      const data = await response.json();
      console.log(data);
    });
  });

  return (
    <div>
      <h3>{}</h3>
    </div>
  );
};

export default CityItem;
