import React, { useState } from "react";
import { BrowserRouter, Link, Switch } from "react-router-dom";
import "./App.css";
import AddCity from "./components/AddCityForm/AddCity";
import Backdrop from "./components/Backdrop/Backdrop";
import CitiesList from "./components/CitiesList";

function App() {
  // const [lat, setLat] = useState("");
  // const [long, setLong] = useState("");
  // const [data, setData] = useState([]);
  // const placesCtx = useContext(PlacesContext);
  const [showBackdrop, setShowBackdrop] = useState(false);

  const showBackdropHandler = () => {
    setShowBackdrop(true);
  };
  const closeBackdropHandler = () => {
    setShowBackdrop(false);
  };

  return (
    <React.Fragment>
      <BrowserRouter>
        <Switch>
          <Route>
            <div className="backgroundImage">
              <Link to="/">
                <h1>Clear Sky Weather App</h1>
              </Link>
              <button id="addButton" onClick={showBackdropHandler}>
                + Add City
              </button>
            </div>

            <Backdrop
              onClose={closeBackdropHandler}
              show={showBackdrop}
            ></Backdrop>

            <AddCity close={closeBackdropHandler} show={showBackdrop}></AddCity>
            <CitiesList></CitiesList>
          </Route>
        </Switch>
      </BrowserRouter>
    </React.Fragment>
  );
}

export default App;
