import React, { useContext, useEffect, useRef, useState } from "react";
import { PlacesContext } from "../context/place-context";
import classes from "./AddCity.module.css";

const AddCity = () => {
  const placesCtx = useContext(PlacesContext);

  useEffect(() => {
    placesCtx.getPlaces();
    console.log("mounted");
  }, []);
  const selectedCountryHandler = (event) => {
    placesCtx.getCitiesOfSelectedCountry(event.target.value);
  };
  const selectedCityHandler = (event) => {
    placesCtx.handleCityIsSelected(event.target.value);
  };
  const addCityHandler = () => {
    placesCtx.addCity();
  };
  return (
    <React.Fragment>
      <div className={classes.container}>
        <select onChange={selectedCountryHandler}>
          <option disabled selected hidden value={null}>
            --Select Country--
          </option>
          {placesCtx.placesData.map((item, index) => {
            return (
              <option key={index} value={item.country}>
                {item.country}
              </option>
            );
          })}
        </select>

        <select
          onChange={selectedCityHandler}
          disabled={!placesCtx.isSelected.country}
        >
          <option disabled selected hidden value={null}>
            --Select City--
          </option>
          {placesCtx.availableCitiesInSelectedCountry.map((city, index) => {
            return (
              <option key={index} value={city}>
                {city}
              </option>
            );
          })}
        </select>
        <button
          onClick={addCityHandler}
          disabled={!placesCtx.isSelected.country || !placesCtx.isSelected.city}
        >
          Add City
        </button>
      </div>
    </React.Fragment>
  );
};

export default AddCity;
