import { useRef, useState } from "react";
import classes from "./AddCity.module.css";

const AddCity = () => {
  const cityRef = useRef();
  const [data, setData] = useState([]);
  const addCity = (event) => {
    event.preventDefault();
    const cityName = cityRef.current.value;

    const requestCity = async () => {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${process.env.REACT_APP_API_KEY}`
      );
      const cityData = await response.json();
      setData(cityData);
    };
    console.log(data);
    console.log(cityName);
    console.log("Submitted");
  };
  return (
    <div className={classes.addCity}>
      <form onSubmit={addCity}>
        <input ref={cityRef} placeholder="Add City"></input>
        <button type="submit">+</button>
      </form>
    </div>
  );
};

export default AddCity;
