import React, { useState } from "react";

export const PlacesContext = React.createContext({
  addedCities: ["London"],
  placesData: [],
  availableCitiesInSelectedCountry: [],
  isSelected: { country: false, city: false },
  selectedCity: null,
  handleCityIsSelected: () => {},
  getPlaces: () => {},
  addCity: () => {},
  removeCity: () => {},
});

const PlacesContextProvider = (props) => {
  const [places, setPlaces] = useState([]);
  const [availableCities, setAvailableCities] = useState([]);
  const [cityToAdd, setCityToAdd] = useState(null);
  const [addedCities, setAddedCities] = useState(["London"]);
  const favoriteCities = ["London"];
  const [isSelected, setIsSelected] = useState({ country: false, city: false });
  console.log("loaded ctx");
  const getPlacesHandler = async () => {
    const response = await fetch(
      "https://countriesnow.space/api/v0.1/countries"
    );
    const places = await response.json();
    setPlaces(places.data);
  };

  const cityIsSelectedHandler = (city) => {
    setIsSelected((prevValue) => ({ ...prevValue, city: true }));
    setCityToAdd(city);
  };
  const addCityHandler = () => {
    favoriteCities.push(cityToAdd);
  };
  const removeCityHandler = () => {};

  return (
    <PlacesContext.Provider
      value={{
        addedCities: favoriteCities,
        placesData: places,
        availableCitiesInSelectedCountry: availableCities,
        isSelected: isSelected,
        selectedCity: cityToAdd,
        handleCityIsSelected: cityIsSelectedHandler,
        getPlaces: getPlacesHandler,
        addCity: addCityHandler,
        removeCity: removeCityHandler,
      }}
    >
      {props.children}
    </PlacesContext.Provider>
  );
};

export default PlacesContextProvider;
