import React, { useState } from "react";

export const PlacesContext = React.createContext({
  countries: [],
  cities: [],
  addedCities: ["London"],
  placesData: [],
  availableCitiesInSelectedCountry: [],
  getPlaces: () => {},
  getCitiesOfSelectedCountry: () => {},
  addCity: () => {},
  removeCity: () => {},
});

const PlacesContextProvider = (props) => {
  const [places, setPlaces] = useState([]);
  const [availableCities, setAvailableCities] = useState([]);
  const getPlacesHandler = async () => {
    const response = await fetch(
      "https://countriesnow.space/api/v0.1/countries"
    );
    const places = await response.json();
    const choose = { country: "Select Country", cities: "Select City" };
    const choosePlaces = choose.push(places.data);
    setPlaces(choosePlaces);
  };
  const getCitiesOfSelectedCountryHandler = (country) => {
    const selectedCountry = places.find(
      (element) => element.country === country
    );
    setAvailableCities(selectedCountry.cities);
  };
  const addCityHandler = () => {};
  const removeCityHandler = () => {};

  return (
    <PlacesContext.Provider
      value={{
        countries: [],
        cities: [],
        addedCities: ["London"],
        placesData: places,
        availableCitiesInSelectedCountry: availableCities,
        getPlaces: getPlacesHandler,
        getCitiesOfSelectedCountry: getCitiesOfSelectedCountryHandler,
        addCity: addCityHandler,
        removeCity: removeCityHandler,
      }}
    >
      {props.children}
    </PlacesContext.Provider>
  );
};

export default PlacesContextProvider;
