import React, { useEffect, useState } from "react";
import "./App.css";
import AddCity from "./components/AddCity";
import CitiesList from "./components/CitiesList";

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");
  const [data, setData] = useState([]);
  const [countries, setCountries] = useState(["Select country"]);
  const [cities, setCities] = useState(["Select City"]);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });

    const getCountries = async () => {
      const response = await fetch(
        "https://countriesnow.space/api/v0.1/countries"
      );
      const info = await response.json();
      const drzave = ["Select Country"];

      for (let i = 0; i < info.data.length; i++) {
        drzave.push(info.data[i].country);
      }
      setCountries(drzave);
    };
    getCountries();
  }, []);

  const getLocation = async () => {
    const address = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${process.env.REACT_APP_API_KEY}`;

    const response = await fetch(address);
    const data = await response.json();
    setData(data);
    console.log(data);
    console.log(data.weather[0].description);
    console.log(data.weather[0].main);
  };

  const fetchCountries = async () => {};

  const selectedCountryHandler = async () => {};

  return (
    <React.Fragment>
      <AddCity></AddCity>
      <CitiesList></CitiesList>
      <button onClick={getLocation}>Add City of my current location </button>
      <div>
        <h3>Latitude is {lat}</h3>
        <h4>Longitude is {long}</h4>
        <h2>{data.name}</h2>
        {/* <p>{data.weather.main}</p> */}
      </div>
      <button onClick={fetchCountries}>fetch all countries and cities</button>

      <select onChange={selectedCountryHandler}>
        {countries.map((country) => {
          if (country === "Select Country") {
            return (
              <option hidden disabled selected>
                Select an option
              </option>
            );
          }
          return <option value={country}>{country}</option>;
        })}
      </select>
      <select></select>
    </React.Fragment>
  );
}

export default App;
