import React, { useEffect, useState } from "react";
import "./App.css";
import AddCity from "./components/AddCity";
import CitiesList from "./components/CitiesList";

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");

  const getWeather = async (lats, longs) => {
    const address = `https://api.openweathermap.org/data/2.5/weather?lat=${lats}&lon=${longs}&appid=${process.env.REACT_APP_API_KEY}`;
    console.log(address);

    const response = await fetch(address);
    const data = await response.json();
    console.log(data);
  };

  const getLocation = () => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);

      const slicedLat = lat.toString().substring(0, 5);
      const slicedLong = long.toString().substring(0, 5);
      console.log(slicedLat);

      getWeather(slicedLat, slicedLong);
    });
  };

  return (
    <React.Fragment>
      <AddCity></AddCity>
      <CitiesList></CitiesList>
      <button onClick={getLocation}>Add my current City</button>
      <div>
        <h3>Latitude is {lat}</h3>
        <h4>Longitude is {long}</h4>
      </div>
    </React.Fragment>
  );
}

export default App;
