import CityItem from "./CityItem";
import classes from "./CitiesList.module.css";
import { useContext, useEffect } from "react";
import { PlacesContext } from "../context/place-context";

const CitiesList = () => {
  const placesCtx = useContext(PlacesContext);

  return (
    <div>
      {placesCtx.cityWeatherInfo.map((city, index) => {
        return (
          <CityItem
            key={index}
            cityName={placesCtx.favoritePlaces[index + 1].cityName}
            cityTemp={city.current.temp}
            timeZone={city.timezone}
          ></CityItem>
        );
      })}
    </div>
  );
};

export default CitiesList;
